package main

//Find the largest product of 5 consecutive numbers in the string below

import (
	"fmt"
	"strconv"
	"strings"
)

func main() {
	var lstring string = ""

	values := strings.Split(lstring, "")
	a := 0
	b := 1
	c := 2
	d := 3
	e := 4
	largest := 0
	cval := 0
	for e < len(values) {
		v, err := strconv.Atoi(values[a])
		w, err := strconv.Atoi(values[b])
		x, err := strconv.Atoi(values[c])
		y, err := strconv.Atoi(values[d])
		z, err := strconv.Atoi(values[e])
		if err == nil {
			cval = v * w * x * y * z
		}
		if cval > largest {
			largest = cval
		}
		a++
		b++
		c++
		d++
		e++
	}
	fmt.Printf("Largest product: %d\n", largest)
}
